private static User LOGGED_IN_USER =
    User.builder().id(1).name("User").username("user").password("password")
        .authorities(singletonList(Authority.builder().name("ROLE_USER").build())).build();

// TODO: Add `.with(documentationAuthorizationCustomizer())` to your http request builder `MockHttpServletRequestBuilder`. For eg., post("/api/todos/{id}/markAsComplete", 1).with(documentationAuthorizationCustomizer()))
// TODO: Add `@AutoConfigureRestDocs` on the test class & also make sure to properly docment API methods, Request & Response entities
// TODO: If you have custom validations & want these to appear in the generated documentation, make sure you add appropriate messages to a file named `org.springframework.restdocs.constraints/ConstraintDescriptions.properties`
/**
 * Required by auto rest docs API to populate authorization snippet with correct values
 *
 * @return request with authorization header
 */
private RequestPostProcessor documentationAuthorizationCustomizer() {
  return request -> {
    if (SecurityContextHolder.getContext().getAuthentication().isAuthenticated()) {
      request.addHeader("Authorization", "Basic " + Base64.getEncoder().encodeToString(
          (LOGGED_IN_USER.getUsername() + ":" + LOGGED_IN_USER.getPassword()).getBytes()));
      documentAuthorization(request, "Basic authentication required");
    }
    return request;
  };
}


// We need to customize default RestDocs such that we can use it with Auto RestDocs so that we get automatic sections that can be included in the code directly
@TestConfiguration
static class CustomizationConfiguration implements RestDocsMockMvcConfigurationCustomizer {

  @Override
  public void customize(MockMvcRestDocumentationConfigurer configurer) {
    // Configuring snippets that gets generated by Spring RestDocs for each endpoint
    List<String> defaultSnippets =
        asList(AUTHORIZATION, PATH_PARAMETERS, REQUEST_PARAMETERS, REQUEST_FIELDS,
            RESPONSE_FIELDS, HTTPIE_REQUEST, HTTP_RESPONSE);

    configurer.snippets()
        .withDefaults(httpieRequest(), httpRequest(), httpResponse(), requestFields(),
            responseFields(), pathParameters(), requestParameters(), description(),
            methodAndPath(),
            sectionBuilder().snippetNames(defaultSnippets).skipEmpty(true).build(),
            authorization("Resource is public"));
  }

  // Configuring result handlers that help us transform result into a meaningful format. For eg., `replaceBinaryContent()` would replace actual binary response content with text `<binary>`
  @Bean
  public RestDocumentationResultHandler restDocumentation(ObjectMapper objectMapper) {
    return document("{class-name}/{method-name}", preprocessRequest(),
        preprocessResponse(replaceBinaryContent(), limitJsonArrayLength(objectMapper),
            prettyPrint()));
  }

  // Will configure the correct object mapper that will be used by auto rest docs to generate metadata about rest endpoints from via `RequestMappingHandlerMethod`
  @Bean
  public MockMvcBuilderCustomizer prepareJacksonForAutoRestDocs(ObjectMapper objectMapper) {
    return builder -> builder.alwaysDo(prepareJackson(objectMapper));
  }

}

